  const contextData = (() => ({
    memesDataState,
    setMemesDataState,
  }), [ memesDataState ]);

  return (
    <>
      <AppContext.Provider value={contextData}>
        {children}
      </AppContext.Provider>
    </>
  )
}

export default AppContextProvider;
