import React from "react";
import Regular from "./components/Regular";
import Hot from "./components/Hot";
import { Route, Routes, NavLink } from "react-router-dom";
import { useState } from "react";
import memes__data from "./memex__data";

function App(props) {
  const [memes, setMemes] = useState(memes__data);

  const setUpvote = (memId) => {
    setMemes((currentMemes) => {
      return currentMemes.map((mem) => {
        if (memId === mem.id) {
          return {
            ...mem,
            upvotes: mem.upvotes + 1,
          };
        }
        return mem;
      });
    });
  };

  const setDownvote = (memId) => {
    setMemes(
      memes.map((mem) => {
        if (memId === mem.id) {
          return {
            ...mem,
            downvotes: mem.downvotes + 1,
          };
        }
        return mem;
      })
    );
  };

  return (
    <div>
      <nav>
        <ul>
          <li>
            <NavLink to="/regular">Regular</NavLink>
          </li>
          <li>
            <NavLink to="/hot">Hot</NavLink>
          </li>
        </ul>
      </nav>
      <Routes>
        <Route
          path="/"
          element={
            <Regular
              memes={memes}
              setDownvote={setDownvote}
              setUpvote={setUpvote}
            />
          }
        />
        <Route
          path="/hot"
          element={
            <Hot
              memes={memes}
              setDownvote={setDownvote}
              setUpvote={setUpvote}
            />
          }
        />
        <Route
          path="/regular"
          element={
            <Regular
              memes={memes}
              setDownvote={setDownvote}
              setUpvote={setUpvote}
            />
          }
        />
      </Routes>
    </div>
  );
}

export default App;
