import React, { useState } from "react";
import Regular from "./components/Regular";
import Hot from "./components/Hot";
import { Route, Routes, NavLink } from "react-router-dom";
import data from "./data";

function App(props) {
  const [memes, setMemes] = useState(data);

  const setUpvote = (memId) => {
    setMemes((prevMemes) => {
      return prevMemes.map((mem) => {
        if (memId === mem.id) {
          return {
            ...mem,
            upvotes: mem.upvotes + 1,
          };
        }
        return mem;
      });
    });
  };

  const setDownvote = (memId) => {
    setMemes(
      memes.map((mem) => {
        if (memId === mem.id) {
          return {
            ...mem,
            downvotes: mem.downvotes + 1,
          };
        }
        return mem;
      })
    );
  };

  return (
    <>
      <nav>
        <ul>
          <li>
            <NavLink to="/regular">Regular</NavLink>
          </li>
          <li>
            <NavLink to="/hot">Hot</NavLink>
          </li>
        </ul>
      </nav>
      <Routes>
        <Route path="/" element={<Regular />} />
        <Route path="/hot" element={<Hot />} />
        <Route path="/regular" element={<Regular />} />
      </Routes>
    </>
  );
}

export default App;
