import { createContext, useState } from "react";
import Memes from "./data";

const AppContext = createContext();

export function AppProvider({ children }) {
  const [data, setData] = useState(Memes);

  const setDataHandler = (value) => {
    setData(value);
  };

  const voteUpHandler = () => {
    setDataHandler((prevData) => {
      prevData.upvotes++;
      return [...prevData];
    });
  };

  const voteDownHandler = () => {
    setDataHandler((prevData) => {
      prevData.downvotes++;
      return [...prevData];
    });
  };

  return (
    <AppContext.Provider
      value={{ data, setDataHandler, voteUpHandler, voteDownHandler }}
    />
  );
}
