import { NavLink } from "react-router-dom";
import styles from "./Navigation__styles.module.css";

import { useState } from "react";


const Navigation = () => {
  const [isNavOpen, setIsNavOpen] = useState("");
  const [navAnimation, setNavAnimation] = useState("");

  const closeMenu = () => {
    setIsNavOpen(false);
    setNavAnimation(styles.hideNav);
  };

  const openMenu = () => {
    setIsNavOpen(true);
    setNavAnimation(styles.showNav);
  };

  const openCloseMenu = () => {
    isNavOpen ? closeMenu() : openMenu();
  };

  return (
    <div className={styles.header}>
      
      <div
        onClick={openCloseMenu}
        className={`${styles.navbar} ${navAnimation}`}
      >
        <NavLink
          activeClassName={styles.active}
          className={styles.link}
          to="/hot"
        >
          GITÓWA!
        </NavLink>
        <NavLink
          activeClassName={styles.active}
          className={styles.link}
          to="/regular"
        >
          takie to jakieś nie wiem...
        </NavLink>
        
      </div>
    </div>
  );
};

export default Navigation;
