export const Meme = (props) => {
  const [voteUpHandler, voteDownHandler] = useContext(AppContext);

  return (
    <div className="memMain">
      <div>
        <h2 className="title">{props.title}</h2>
        <img className="image" src={props.mem.img} alt="random pic" />
        <div className="buttons">
          <button className="upvote" onClick={voteUpHandler(props.upvotes)}>
            HOT
          </button>
          <button
            className="downvote"
            onClick={voteDownHandler(props.downvotes)}
          >
            meh
          </button>
        </div>
      </div>
    </div>
  );
};

export default Meme;
